    if "csv_data" in st.session_state and st.session_state.csv_data is not None:
        st.subheader("üìä X, Y Ï∂ï ÏÑ†ÌÉù Î∞è Ï†ïÏ†Å Í∑∏ÎûòÌîÑ")
        columns = st.session_state.csv_data.columns.tolist()
